import { Component } from "react";
import serviceData from "../../services/Data";
import AddToDoItem from "../AddToDoItem/AddToDoItem";
import ToDoListItem from "../ToDoListItem/ToDoListItem";
import "./toDoList.css";

export default class ToDoList extends Component {
  state = {
    todos: [],
  };

  componentDidMount() {
    this.createElements();
  }
  componentDidUpdate() {
    this.createElements();
  }

  addItem = (title, details) => {
    serviceData
      .createPost({
        title,
        details,
      })
      .catch((error) => console.error(error));
  };
  updateItem = (id, data) => {
    serviceData.updatePost(id, data).catch((error) => console.error(error));
  };
  deleteItem = (id) => {
    serviceData.deletePost(id).catch((error) => console.error(error));
  };

  createElements = () => {
    serviceData
      .getAllPosts()
      .then((data) => {
        this.setState({
          todos: data.map((todo) => {
            let doneStyle = todo.done ? "isDone" : "notDone";
            return (
              <ToDoListItem
                id={todo.id}
                key={todo.id}
                title={todo.title}
                details={todo.details}
                done={todo.done}
                doneStyle={doneStyle}
                updateItem={this.updateItem}
                deleteItem={this.deleteItem}
                toggleDone={serviceData.toggleDone}
                getSinglePost={serviceData.getSinglePost}
              />
            );
          }),
        });
      })
      .catch((error) => console.error(error));
  };

  render() {
    return (
      <div className="list__container">
        <AddToDoItem addItem={this.addItem} />
        <ul className="show-list">{this.state.todos}</ul>
      </div>
    );
  }
}
