import React, { Component } from "react";
import withRouter from "../withRoutr/withRouter";
import Input from "../Input/Input";
import { Todo } from "../../types/todoObject";
import { TodoDone } from "../../types/todoDone";
import done from "../../asset/img/done.png";
import edit from "../../asset/img/edit.png";
import del from "../../asset/img/delete.png";
import "./toDoLIstItem.css";

interface IProps {
  router: {
    navigate: (path: string) => void
  },
  deleteItem: (id: string) => void,
  updateItem: (id: string, data: Todo | TodoDone) => void,
  toggleDone: (id: string) => Promise<TodoDone>,
  getSinglePost: (id: string) => Promise<Todo>,
  title: string,
  details: string,
  doneStyle:string,
  id: string
}

interface IState {
  title: string,
  details: string
}

class ToDoListItem extends Component<IProps, IState> {
  state = {
    title: this.props.title ? this.props.title : "Title",
    details: this.props.details ? this.props.details : "Details",
  };

  onDelete = (e: React.ChangeEvent<HTMLButtonElement>) => {
    this.props.deleteItem(e.target.getAttribute("id") || "");
  };
  onUpdate = (e:React.ChangeEvent<HTMLButtonElement>) => {
    this.props.updateItem(e.target.getAttribute("id") || "", {
      title: this.state.title,
      details: this.state.details,
    });
  };
  isDone = (e:React.ChangeEvent<HTMLButtonElement>) => {
    const updateDone = (boolData:boolean) => {
      this.props.updateItem(e.target.getAttribute("id") || "", {
        done: boolData,
      });
    };

    this.props.toggleDone(e.target.getAttribute("id") || "").then((res) => {
      if (res.done) {
        updateDone(false);
      } else {
        updateDone(true);
      }
    });
  };

  showDetails = (e:React.MouseEventHandler<HTMLButtonElement>) => {
    this.props
      .getSinglePost(e.getAttribute("id") || "")
      .then((res) => {
        localStorage.setItem(
          "details",
          res.details ? res.details : "There are no details"
        );
      })
      .then(() => {
        this.props.router.navigate("/details");
      });
  };

  handleInputChanged = (e:React.ChangeEvent<HTMLInputElement>) => {
    this.setState({
      [e.target.name]: e.target.value,
    } as unknown as IState);
  };

  render() {
    const { title, details } = this.state;
    let itemStyle = `todo-item  ${this.props.doneStyle}`;
    return (
      <li className={itemStyle}>
        <div className="title">
          <Input
            name="title"
            type="text"
            placeholder="Title"
            value={title}
            onChange={this.handleInputChanged}
          />
          <Input
            name="details"
            type="text"
            placeholder="Details"
            value={details}
            onChange={this.handleInputChanged}
          />
          <button id={this.props.id} onClick={this.showDetails}>
            reade more
          </button>
        </div>
        <div className="icons">
          <button className="edit">
            <img
              src={done}
              alt="Done icon"
              title="Done"
              id={this.props.id}
              onClick={() => this.isDone}
            />
          </button>
          <button className="edit">
            <img
              src={edit}
              alt="Edit icon"
              title="Edit"
              id={this.props.id}
              onClick={() => this.onUpdate}
            />
          </button>
          <button className="delete">
            <img
              src={del}
              alt="Delete icon"
              title="Delete"
              id={this.props.id}
              onClick={() => this.onDelete}
            />
          </button>
        </div>
      </li>
    );
  }
}

export default withRouter(ToDoListItem);
